import React, { useState, useEffect } from 'react';
import {
  AppBar,
  Toolbar,
  Typography,
  Button,
  Box,
  IconButton,
  InputBase,
  Slide,
} from '@mui/material';
import { Link as RouterLink, useLocation } from 'react-router-dom';
import SearchIcon from '@mui/icons-material/Search';
import { useSearch } from '../../context/SearchContext';

const Navbar = () => {
  const [showSearch, setShowSearch] = useState(false);
  const [inputValue, setInputValue] = useState('');
  const { handleSearch, searchTerm } = useSearch();
  const location = useLocation();

  // Sync input value with URL search param when on marketplace
  useEffect(() => {
    if (location.pathname === '/marketplace') {
      const params = new URLSearchParams(location.search);
      const searchQuery = params.get('search') || '';
      setInputValue(searchQuery);
      setShowSearch(!!searchQuery);
    } else {
      setInputValue('');
      setShowSearch(false);
    }
  }, [location]);

  const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const newValue = event.target.value;
    setInputValue(newValue);
    handleSearch(newValue);
  };

  return (
    <AppBar 
      position="fixed" 
      sx={{ 
        bgcolor: 'rgba(22, 28, 36, 0.8)',
        backdropFilter: 'blur(6px)',
      }}
    >
      <Toolbar sx={{ justifyContent: 'space-between' }}>
        <Typography 
          variant="h6" 
          component={RouterLink} 
          to="/"
          sx={{ 
            textDecoration: 'none',
            color: 'inherit',
            fontWeight: 'bold'
          }}
        >
          NFT Marketplace
        </Typography>

        <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
          <Slide direction="down" in={showSearch} mountOnEnter unmountOnExit>
            <Box
              sx={{
                position: 'relative',
                bgcolor: 'rgba(255, 255, 255, 0.15)',
                borderRadius: 1,
                px: 2,
                mx: 1,
              }}
            >
              <InputBase
                placeholder="Search NFTs..."
                value={inputValue}
                onChange={handleInputChange}
                sx={{
                  color: 'inherit',
                  '& .MuiInputBase-input': {
                    p: 1,
                    width: '200px',
                    '&::placeholder': {
                      color: 'rgba(255, 255, 255, 0.7)',
                    },
                  },
                }}
              />
            </Box>
          </Slide>

          <IconButton 
            color="inherit"
            onClick={() => {
              setShowSearch(!showSearch);
              if (!showSearch) {
                setInputValue('');
                handleSearch('');
              }
            }}
            sx={{ 
              bgcolor: showSearch ? 'rgba(255, 255, 255, 0.15)' : 'transparent',
              '&:hover': {
                bgcolor: 'rgba(255, 255, 255, 0.25)',
              }
            }}
          >
            <SearchIcon />
          </IconButton>

          <Button
            color="inherit"
            component={RouterLink}
            to="/marketplace"
            sx={{
              '&:hover': {
                bgcolor: 'rgba(255, 255, 255, 0.1)',
              }
            }}
          >
            Marketplace
          </Button>
          <Button
            color="inherit"
            component={RouterLink}
            to="/create-nft"
            sx={{
              '&:hover': {
                bgcolor: 'rgba(255, 255, 255, 0.1)',
              }
            }}
          >
            Create NFT
          </Button>
          <Button
            color="inherit"
            component={RouterLink}
            to="/profile"
            sx={{
              '&:hover': {
                bgcolor: 'rgba(255, 255, 255, 0.1)',
              }
            }}
          >
            Profile
          </Button>
        </Box>
      </Toolbar>
    </AppBar>
  );
};

export default Navbar;
