import React, { useState } from 'react';
import { 
  AppBar, 
  Toolbar, 
  Typography, 
  Button, 
  Box,
  IconButton,
  InputBase,
  Paper,
  Fade
} from '@mui/material';
import { Link as RouterLink } from 'react-router-dom';
import SearchIcon from '@mui/icons-material/Search';

const Navbar = () => {
  const [showSearch, setShowSearch] = useState(false);

  return (
    <AppBar position="fixed" sx={{ 
      background: 'rgba(0, 0, 0, 0.5)',
      backdropFilter: 'blur(8px)',
      boxShadow: 'none',
    }}>
      <Toolbar>
        <Typography
          variant="h6"
          component={RouterLink}
          to="/"
          sx={{
            flexGrow: 1,
            textDecoration: 'none',
            color: 'inherit',
            fontWeight: 'bold',
          }}
        >
          NFT Marketplace
        </Typography>

        <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
          {/* Search Bar */}
          <Fade in={showSearch}>
            <Paper
              component="form"
              sx={{
                p: '2px 4px',
                display: showSearch ? 'flex' : 'none',
                alignItems: 'center',
                width: 400,
                bgcolor: 'rgba(255, 255, 255, 0.15)',
                border: '1px solid rgba(255, 255, 255, 0.3)',
              }}
            >
              <InputBase
                sx={{ ml: 1, flex: 1, color: 'inherit' }}
                placeholder="Search NFTs..."
                inputProps={{ 'aria-label': 'search nfts' }}
              />
            </Paper>
          </Fade>

          {/* Search Toggle Button */}
          <IconButton 
            color="inherit"
            onClick={() => setShowSearch(!showSearch)}
            sx={{ 
              bgcolor: showSearch ? 'rgba(255, 255, 255, 0.15)' : 'transparent',
              '&:hover': {
                bgcolor: 'rgba(255, 255, 255, 0.25)',
              }
            }}
          >
            <SearchIcon />
          </IconButton>

          {/* Navigation Buttons */}
          <Button
            color="inherit"
            component={RouterLink}
            to="/marketplace"
            sx={{
              '&:hover': {
                bgcolor: 'rgba(255, 255, 255, 0.1)',
              }
            }}
          >
            Marketplace
          </Button>
          <Button
            color="inherit"
            component={RouterLink}
            to="/create-nft"
            sx={{
              '&:hover': {
                bgcolor: 'rgba(255, 255, 255, 0.1)',
              }
            }}
          >
            Create NFT
          </Button>
          <Button
            color="inherit"
            component={RouterLink}
            to="/profile"
            sx={{
              '&:hover': {
                bgcolor: 'rgba(255, 255, 255, 0.1)',
              }
            }}
          >
            Profile
          </Button>
        </Box>
      </Toolbar>
    </AppBar>
  );
};

export default Navbar;
