import React, { useState } from 'react';
import { 
  Container, 
  Typography, 
  Box, 
  Grid, 
  Card, 
  CardMedia,
  Button,
  IconButton,
  CardActionArea,
  Stack,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import VerifiedIcon from '@mui/icons-material/Verified';
import KeyboardArrowLeft from '@mui/icons-material/KeyboardArrowLeft';
import KeyboardArrowRight from '@mui/icons-material/KeyboardArrowRight';
import AddIcon from '@mui/icons-material/Add';
import RemoveIcon from '@mui/icons-material/Remove';

// Featured NFT data
const featuredNFT = {
  title: "Abstract Thought of Art",
  creator: "ZafGod.eth",
  platform: "Abstract",
  price: "0.00069 ETH",
  description: "I explore the intersection of digital decay, cybernetic spirituality, and the neon ghosts of an unraveling reality. This piece is a synthetic dreamscape—part ritual, part hallucination—where fragmented figures, glitching flora, and...",
  image: "https://via.placeholder.com/800x600/1a237e/ffffff",
  status: "MINTING NOW",
  mintedCount: "11",
  maxPerWallet: "10",
  timeLeft: "2d 23h",
  isVerified: true
};

// Mock data for latest drops
const allNFTs = [
  {
    id: 1,
    title: 'Abstract Thought of Art',
    creator: 'ZafGod.eth',
    price: '0.00069 ETH',
    image: 'https://via.placeholder.com/400x400/1a237e/ffffff',
    status: 'Now',
    isVerified: true
  },
  {
    id: 2,
    title: 'Harvested Opulence',
    creator: 'Fame Identity',
    price: '0.005 ETH',
    image: 'https://via.placeholder.com/400x400/4a148c/ffffff',
    status: '23 hours',
    isVerified: true
  },
  {
    id: 3,
    title: 'RELAX',
    creator: 'brain pasta',
    price: '0.0038 ETH',
    image: 'https://via.placeholder.com/400x400/311b92/ffffff',
    status: 'Now',
    isVerified: true
  },
  {
    id: 4,
    title: 'Spring will come',
    creator: 'Reza Milani',
    price: '0.0004 ETH',
    image: 'https://via.placeholder.com/400x400/6a1b9a/ffffff',
    status: 'Now',
    isVerified: true
  },
  {
    id: 5,
    title: 'Cyber Dreams',
    creator: 'neon.eth',
    price: '0.0089 ETH',
    image: 'https://via.placeholder.com/400x400/00838f/ffffff',
    status: 'Now',
    isVerified: true
  },
  {
    id: 6,
    title: 'Digital Wilderness',
    creator: 'artmaster',
    price: '0.0123 ETH',
    image: 'https://via.placeholder.com/400x400/2e7d32/ffffff',
    status: '2 hours',
    isVerified: true
  },
  {
    id: 7,
    title: 'Neon Nights',
    creator: 'pixelart.eth',
    price: '0.0075 ETH',
    image: 'https://via.placeholder.com/400x400/c2185b/ffffff',
    status: 'Now',
    isVerified: true
  },
  {
    id: 8,
    title: 'Future Past',
    creator: 'retro.eth',
    price: '0.0055 ETH',
    image: 'https://via.placeholder.com/400x400/d84315/ffffff',
    status: '5 hours',
    isVerified: true
  },
  {
    id: 9,
    title: 'Quantum Dreams',
    creator: 'quantum.art',
    price: '0.0095 ETH',
    image: 'https://via.placeholder.com/400x400/4527a0/ffffff',
    status: 'Now',
    isVerified: true
  },
  {
    id: 10,
    title: 'Virtual Reality',
    creator: 'vr.master',
    price: '0.0082 ETH',
    image: 'https://via.placeholder.com/400x400/00695c/ffffff',
    status: '12 hours',
    isVerified: true
  },
  {
    id: 11,
    title: 'Digital Genesis',
    creator: 'crypto.art',
    price: '0.0067 ETH',
    image: 'https://via.placeholder.com/400x400/bf360c/ffffff',
    status: 'Now',
    isVerified: true
  },
  {
    id: 12,
    title: 'Pixel Paradise',
    creator: 'pixel.master',
    price: '0.0043 ETH',
    image: 'https://via.placeholder.com/400x400/0277bd/ffffff',
    status: '8 hours',
    isVerified: true
  }
];

const ITEMS_PER_PAGE = 5;

const Home = () => {
  const navigate = useNavigate();
  const [hoveredId, setHoveredId] = useState<number | null>(null);
  const [activeStep, setActiveStep] = useState(0);
  const [isAnimating, setIsAnimating] = useState(false);
  const [slideDirection, setSlideDirection] = useState<'left' | 'right'>('right');
  const [mintQuantity, setMintQuantity] = useState(1);
  const maxSteps = allNFTs.length;

  const handleNext = () => {
    if (isAnimating || activeStep >= maxSteps - 1) return;
    setIsAnimating(true);
    setSlideDirection('right');
    setActiveStep((prevStep) => prevStep + 1);
    setTimeout(() => setIsAnimating(false), 500); // Match this with animation duration
  };

  const handleBack = () => {
    if (isAnimating || activeStep <= 0) return;
    setIsAnimating(true);
    setSlideDirection('left');
    setActiveStep((prevStep) => prevStep - 1);
    setTimeout(() => setIsAnimating(false), 500); // Match this with animation duration
  };

  // Calculate which NFTs to show with one item sliding
  const visibleNFTs = allNFTs.slice(
    Math.max(0, activeStep - 2),
    Math.min(activeStep + 3, allNFTs.length)
  );

  return (
    <Container maxWidth="xl" sx={{ mt: 4, pb: 8 }}>
      {/* Featured NFT Section */}
      <Box
        sx={{
          position: 'relative',
          mb: 8,
          borderRadius: 4,
          overflow: 'hidden',
          background: 'linear-gradient(135deg, #00ff9d 0%, #00ff9d40 100%)',
          boxShadow: '0 8px 32px rgba(0, 255, 157, 0.1)',
        }}
      >
        <Grid container spacing={0}>
          <Grid item xs={12} md={6} sx={{ p: 4 }}>
            <Typography 
              variant="caption" 
              sx={{ 
                bgcolor: 'rgba(0, 0, 0, 0.2)',
                color: '#00ff9d',
                px: 2,
                py: 0.5,
                borderRadius: 2,
                mb: 2,
                display: 'inline-block'
              }}
            >
              {featuredNFT.status}
            </Typography>
            
            <Typography variant="h3" component="h1" sx={{ mt: 2, mb: 1, fontWeight: 'bold', color: '#000' }}>
              {featuredNFT.title}
            </Typography>

            <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 3 }}>
              <Typography sx={{ color: '#000' }}>by</Typography>
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 0.5 }}>
                <Typography sx={{ fontWeight: 'bold', color: '#000' }}>{featuredNFT.creator}</Typography>
                {featuredNFT.isVerified && <VerifiedIcon sx={{ fontSize: 16, color: '#2196f3' }} />}
              </Box>
              <Typography sx={{ color: '#000' }}>on</Typography>
              <Typography sx={{ fontWeight: 'bold', color: '#000' }}>{featuredNFT.platform}</Typography>
            </Box>

            <Typography sx={{ mb: 4, color: '#000', opacity: 0.8 }}>
              {featuredNFT.description}
              <Button sx={{ color: '#000', textTransform: 'none', p: 0, ml: 1 }}>
                Show more
              </Button>
            </Typography>

            <Box sx={{ 
              bgcolor: 'rgba(255, 255, 255, 0.1)',
              borderRadius: 2,
              p: 2,
              mb: 3
            }}>
              <Stack direction="row" alignItems="center" spacing={2}>
                <IconButton 
                  size="small"
                  onClick={() => setMintQuantity(Math.max(1, mintQuantity - 1))}
                  sx={{ bgcolor: 'rgba(0, 0, 0, 0.1)' }}
                >
                  <RemoveIcon />
                </IconButton>
                <Typography sx={{ color: '#000', fontWeight: 'bold' }}>{mintQuantity}</Typography>
                <IconButton 
                  size="small"
                  onClick={() => setMintQuantity(Math.min(10, mintQuantity + 1))}
                  sx={{ bgcolor: 'rgba(0, 0, 0, 0.1)' }}
                >
                  <AddIcon />
                </IconButton>
                <Button
                  variant="contained"
                  sx={{
                    bgcolor: '#000',
                    color: '#fff',
                    px: 4,
                    py: 1,
                    borderRadius: 2,
                    '&:hover': {
                      bgcolor: '#000',
                    }
                  }}
                >
                  Mint for {featuredNFT.price}
                </Button>
              </Stack>
            </Box>

            <Stack direction="row" spacing={3} sx={{ color: '#000' }}>
              <Box>
                <Typography variant="caption">Minted</Typography>
                <Typography variant="body2" sx={{ fontWeight: 'bold' }}>{featuredNFT.mintedCount} minted</Typography>
              </Box>
              <Box>
                <Typography variant="caption">Per wallet</Typography>
                <Typography variant="body2" sx={{ fontWeight: 'bold' }}>{featuredNFT.maxPerWallet} per wallet</Typography>
              </Box>
              <Box>
                <Typography variant="caption">Time left</Typography>
                <Typography variant="body2" sx={{ fontWeight: 'bold' }}>{featuredNFT.timeLeft} left</Typography>
              </Box>
            </Stack>
          </Grid>

          <Grid item xs={12} md={6} sx={{ p: 4 }}>
            <Box
              sx={{
                width: '100%',
                height: '100%',
                minHeight: 400,
                borderRadius: 2,
                overflow: 'hidden',
                boxShadow: '0 8px 32px rgba(0, 0, 0, 0.1)',
              }}
            >
              <img 
                src={featuredNFT.image} 
                alt={featuredNFT.title}
                style={{
                  width: '100%',
                  height: '100%',
                  objectFit: 'cover',
                }}
              />
            </Box>
          </Grid>
        </Grid>
      </Box>

      {/* Latest Drops Section */}
      <Box sx={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center',
        mb: 4
      }}>
        <Typography variant="h5" component="h1" sx={{ fontWeight: 'bold' }}>
          Latest drops
        </Typography>
      </Box>

      {/* NFT Grid with Navigation */}
      <Box sx={{ position: 'relative', width: '100%', overflow: 'hidden' }}>
        {/* Left Navigation Arrow */}
        <IconButton
          onClick={handleBack}
          disabled={activeStep === 0 || isAnimating}
          sx={{
            position: 'absolute',
            left: -20,
            top: '50%',
            transform: 'translateY(-50%)',
            bgcolor: 'rgba(0, 0, 0, 0.5)',
            color: 'white',
            zIndex: 2,
            '&:hover': {
              bgcolor: 'rgba(0, 0, 0, 0.7)',
            },
            '&.Mui-disabled': {
              display: 'none'
            }
          }}
        >
          <KeyboardArrowLeft />
        </IconButton>

        {/* Right Navigation Arrow */}
        <IconButton
          onClick={handleNext}
          disabled={activeStep >= maxSteps - 1 || isAnimating}
          sx={{
            position: 'absolute',
            right: -20,
            top: '50%',
            transform: 'translateY(-50%)',
            bgcolor: 'rgba(0, 0, 0, 0.5)',
            color: 'white',
            zIndex: 2,
            '&:hover': {
              bgcolor: 'rgba(0, 0, 0, 0.7)',
            },
            '&.Mui-disabled': {
              display: 'none'
            }
          }}
        >
          <KeyboardArrowRight />
        </IconButton>

        {/* NFT Grid */}
        <Box
          sx={{
            display: 'flex',
            transition: 'transform 0.5s ease-in-out',
            transform: `translateX(-${activeStep * (100 / 5)}%)`,
            ml: 0,
          }}
        >
          {allNFTs.map((nft) => (
            <Box
              key={nft.id}
              sx={{
                flex: `0 0 ${100 / 5}%`,
                padding: '0 8px',
                transition: 'all 0.5s ease-in-out',
                opacity: 1,
                transform: 'scale(1)',
              }}
            >
              <Card 
                sx={{ 
                  bgcolor: 'rgba(22, 28, 36, 0.95)',
                  borderRadius: 2,
                  position: 'relative',
                  aspectRatio: '1/1',
                  cursor: 'pointer',
                  transition: 'transform 0.3s ease-in-out',
                  '&:hover': {
                    transform: 'scale(1.02)'
                  }
                }}
                onMouseEnter={() => setHoveredId(nft.id)}
                onMouseLeave={() => setHoveredId(null)}
              >
                <CardActionArea>
                  <CardMedia
                    component="img"
                    image={nft.image}
                    alt={nft.title}
                    sx={{ 
                      height: '100%',
                      width: '100%',
                      aspectRatio: '1/1'
                    }}
                  />
                  {hoveredId === nft.id && (
                    <Box
                      sx={{
                        position: 'absolute',
                        top: 0,
                        left: 0,
                        right: 0,
                        bottom: 0,
                        bgcolor: 'rgba(0, 0, 0, 0.5)',
                        display: 'flex',
                        justifyContent: 'center',
                        alignItems: 'center',
                        transition: 'all 0.3s ease-in-out'
                      }}
                    >
                      <Button
                        variant="contained"
                        sx={{
                          bgcolor: 'white',
                          color: 'black',
                          '&:hover': {
                            bgcolor: 'white',
                          },
                          borderRadius: '20px',
                          px: 3
                        }}
                      >
                        Buy Now
                      </Button>
                    </Box>
                  )}
                </CardActionArea>
                <Box sx={{ 
                  position: 'absolute', 
                  bottom: 0, 
                  left: 0, 
                  right: 0,
                  p: 2,
                  background: 'linear-gradient(to top, rgba(0,0,0,0.8) 0%, rgba(0,0,0,0) 100%)'
                }}>
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                    <Typography variant="subtitle2" sx={{ color: 'grey.400' }}>
                      {nft.creator}
                    </Typography>
                    {nft.isVerified && (
                      <VerifiedIcon sx={{ fontSize: 16, color: '#2196f3' }} />
                    )}
                  </Box>
                  <Typography variant="subtitle1" sx={{ color: 'white', fontWeight: 'bold' }}>
                    {nft.title}
                  </Typography>
                  <Box sx={{ 
                    display: 'flex', 
                    justifyContent: 'space-between',
                    alignItems: 'center',
                    mt: 1 
                  }}>
                    <Box>
                      <Typography variant="caption" sx={{ color: 'grey.400' }}>
                        Minting
                      </Typography>
                      <Typography variant="body2" sx={{ color: 'white', fontWeight: 'bold' }}>
                        {nft.price}
                      </Typography>
                    </Box>
                    <Typography 
                      variant="caption" 
                      sx={{ 
                        color: nft.status === 'Now' ? '#4caf50' : 'white',
                        fontWeight: 'bold'
                      }}
                    >
                      • {nft.status}
                    </Typography>
                  </Box>
                </Box>
              </Card>
            </Box>
          ))}
        </Box>
      </Box>
    </Container>
  );
};

export default Home;
